import React , { useState, useEffect }from 'react'
import '../styles/Home.css'
import im1 from "../assets/imagechat1.jpg"
function home() {
  return (
    <div>homeWelcome to our Website ! </div>

  )
  

const ImageRotator = () => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const images = [
   im1 , 
   im2
    
  ];

  useEffect(() => {
    // Mettez en place une logique pour faire tourner les images à intervalles réguliers
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 2000);

    return () => {
      clearInterval(interval); // Nettoyer l'intervalle lors de la suppression du composant
    };
  }, []);

  return (
    <div>
      <img src={images[currentImageIndex]} alt="Rotating Image" />
    </div>
  );
};



}

export default home
