import React , {useState} from 'react'
import {BrowserRouter as Router ,Route , Routes ,Link , useNavigate } from 'react-router-dom'; 
import Logo from '../assets/logo.png'
import Signin from './signin';
import '../styles/signup.css'; 
import axios from 'axios';

function Signup() {
 
  const [formData, setFormData] = useState({
    Firstname: '',
    Lastname:'',
    Email: '', 
    password: ''
  });
  const [message, setMessage] = useState('');
  const [Firstname, setFirstname] = useState('');
  const [Lastname, setLastname] = useState('');
  const [Email, setEmail] = useState('');
  const [password, setpassword] = useState('');
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };
  const navigate = useNavigate();

  const handleSubmit = async () => {
    try {
      
      const response = await axios.post('http://127.0.0.1:5000/create', formData); // Send the POST request
      const token = response.data.token; // Get the token from the response

      // Log the token for debugging purposes
      console.log('Token:', token);

      // Now you can navigate to the dashboard page
      navigate.push('/dashboard');
    } catch (error) {
      console.error('Error:', error);
      // Handle the error here
    }
  };
  
  return (
    
    <form className="signup-form">
    <div className="image-container">
      <img src={Logo} className="logo" alt="Logo Chatbotyy" />
    </div>
    <div className="form-container">
      <div className="form-outline form-inline">
        <input type="text" id="firstName" className="form-control form-control-lg" placeholder="First Name" value={Firstname} />
     
        <input type="text" id="lastName" className="form-control form-control-lg" placeholder="Last Name" value={Firstname} />
      </div>
      <div className="form-outline">
        <input type="text" id="email" className="form-control form-control-lg" placeholder="Email Address" />
      </div>
      <div className="form-outline">
        <input type="password" id="password" className="form-control form-control-lg" placeholder="Password" />
      </div>
    </div>
    <div className="button-container">
      <button type="button" className="btn btn-outline-light btn-block btn-lg mb-6" onClick={handleSubmit}>
        Sign up
      </button>
    </div>
    <br></br>
    <div className="text-center text-white">
      <p>
        Already a member? <Link to="/Signin" className="text-light">Sign In Now</Link>
      </p>
      <Routes>
        <Route path="/Signin" element={<Signin />} />
      </Routes>
    </div>
  </form>
 
  )
}

export default Signup
