import React , {useState} from 'react'
import Sidebar from '../components/Sidebar'
import Logout from '../assets/logout.png'
import Chat from '../components/chat'
function Dashboard() {
  const [color1, setColor1] = useState('#FFFFFF');
  const [color2, setColor2] = useState('#FFFFFF');
  const handleColorChange1 = (event) => {
    setColor1(event.target.value);
  };
  const handleColorChange2 = (event) => {
    setColor2(event.target.value);
  };
  const handleFileChange = (event) => {
    const file = event.target.files[0];
    console.log('Uploaded image:', file);
  };

    const [isChatOpen, setIsChatOpen] = useState(true);

    const handleToggleChat = () => {
    setIsChatOpen(prevState => !prevState);
  };
  return (

   <div>
  
    <button className=' btn btn-outline-light' style={{  position: 'fixed',top:' 7%', right: '1%',}}> 
    <img   src={Logout} className='img' style={{ width: '25px', height: '25px' }} alt="Logout"  /> 
    </button>
    <div><Sidebar/></div>
    <div class="col-md mb-4">
     <div   class="form-outline">
     <div style={{border: '1px solid white',padding :'10px',position: 'fixed',top:' 7%', left: '15%'}}>
    
     <input type="text" class="form-control form-control-lg"  placeholder='Chatbot name '/>
  
     <div style={{marginTop:"3%"}}>
     <label className='text text-light ' style={{ marginRight:"2%"}}> Select your chatbot name color : </label>
     <input type="color"      value={color1} onChange={handleColorChange1}/></div>
     
     <label className='text text-light ' style={{ marginRight:"2%"}}> Select your chatbot name color : </label>
     <input type="color"      value={color2} onChange={handleColorChange2}/>
      <button className='btn btn-outline-light'> <input type="file" accept=".png,.jpg,.jpeg" onChange={handleFileChange} /> </button>
     </div>
     <div style={{border: '1px solid white', top:'40px ' , position: 'fixed', right: '15%' , backgroundColor : 'transparent' }}>
     <div><button type="button" class="btn-close " aria-label="Close" id="closeButton" onClick={handleToggleChat}></button>
              </div>
     <div style={{width : '500 px ' , height : '100%'}}> <Chat/></div>
    </div>
     </div>
    
     
   
    </div>
    
  </div>


  
  )
}

export default Dashboard
