import React ,  { useState, useEffect } from 'react'
import axios from 'axios';
import Sidebar  from '../components/Sidebar'
import '../styles/chatbot.css' ;
import Logout from '../assets/logout.png'
import { Link } from 'react-router-dom';  
function Chatbots() {
  const [chatbots, setChatbots] = useState([])
  useEffect(() => {
    async function fetchChatbots() {
      try {
        const token = localStorage.getItem('token'); 
        const headers = {
          Authorization: `Bearer ${token}`,
        };

        const response = await axios.get('/chatbots', {
          headers: headers,
        });

        setChatbots(response.data);
      } catch (error) {
        console.error('Error fetching chatbots:', error);
      }
    }

    fetchChatbots();
  }, []);
  const handleLogout = async () => {
    try {
          await axios.get('http://127.0.0.1:5000/logout');

       
        window.location.href = '/';  } catch (error) {
        console.error('Logout error:', error);
        
    }
};


  return (
    <div > 
    <div  style={{top:'8% '  , position:'absolute'}}><Sidebar/></div>
    <button className=' btn btn-outline-light' style={{ position:'absolute',  top:'8%', left: '90%', fontSize: '12px' }} onClick={handleLogout}> 
         <img   src={Logout} style={{ width: '25px', height: '25px' }} alt="Logout"  /> 
       
       </button>
     <h2 style={{ color: 'white' , top:'12%' , textAlign: 'center' ,left:'13%' , position: 'absolute'}}> Your chatbots :</h2>
    
    <div style={{ top:'15%' , zIndex:'1', display: 'flex',left:'15%', maxWidth:'70%',maxHeight:'600px',
      justifyContent: 'center', alignItems:'center' ,  position:'relative' , overflowY:'auto' }}>
    
        <table  class="table ">
        <thead>
        <tr >
      <th scope="col"></th>
      <th scope="col"></th>
      <th scope="col">name</th>
      <th scope="col"></th>
      <th scope="col"></th>
    </tr>
  </thead>
  
  <tbody >
  {chatbots.map((chatbot, index) => (
    
              <tr key={chatbot.id}>
                <th scope="row">{index + 1}</th>
                <td>  <div className="avatar">
          <img src={chatbot.photo_data} alt="Chatbot Avatar" />
        </div> </td>
          <td> {chatbot.name}</td>
          <td> {chatbot.id}</td>
         
                <td>
                  <Link to={`/Chatpage/${chatbot.id}`} className="btn btn-gradient mr-6"  style={{ marginRight: '5px' }} >Read</Link> 
                  <button className="btn btn-gradient mr-2">Update</button>{' '} 
                 <button className="btn btn-gradient">Delete</button>
                </td>
              </tr>
            ))}
    </tbody>
  


        </table>
      
    </div>
    </div>
  )
}

export default Chatbots
