import React , {useState} from 'react'
import Sidebar from '../components/Sidebar'
import Logout from '../assets/logout.png'
import Chat from '../components/chat'
import Logo from '../assets/cha (2).png'
import { Link} from 'react-router-dom';
import {BrowserRouter as Router ,Route , Routes } from 'react-router-dom'; 
import Profil from './profil'
import '../styles/dashboard.css'
function Dashboard() {
  const [color1, setColor1] = useState('#FFFFFF');
  const [color2, setColor2] = useState('#CFDEF2');
  const [color3, setColor3] = useState('#03224c');
  const [color4, setColor4] = useState('#03224c');
  const [color5, setColor5] = useState('#FFFFFF');
  const [selectedFont, setSelectedFont] = useState('Times New Roman');
  const [logoSrc, setLogoSrc] = useState(Logo);
  const [textInput, setTextInput] = useState('chatbotty');
  const [textInput2, setTextInput2] = useState();
 
  const handleFontChange = (event) => {
    const newFont = event.target.value;
    setSelectedFont(newFont);
  };
  const handleColorChange1 = (event) => {
    const newColor1 = event.target.value ; 
    setColor1(newColor1);
  };
  const handleColorChange2 = (event) => {
    const newColor2 = event.target.value ; 
    setColor2(newColor2);
  };
  const handleColorChange3 = (event) => {
    const newColor3 = event.target.value ; 
    setColor3(newColor3);
  };
  const handleColorChange4 = (event) => {
    const newColor4 = event.target.value ; 
    setColor4(newColor4);
  };
  const handleColorChange5 = (event) => {
    const  newColor5 =  event.target.value ; 
    setColor5(newColor5);
  };
  const handleFileChange = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();
    reader.onloadend = () => {
      setLogoSrc(reader.result);
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  };
  const [isChatOpen, setIsChatOpen] = useState(true);
      
        const handleToggleChat = () => {
          setIsChatOpen(prevState => !prevState);
        };
       

        const handleTextChange = (event) => {
          setTextInput(event.target.value);
        };
      
  return (

   <div className="dashboard-container">
   
       {/* <button className=' btn btn-outline-light' style={{  top:' 7%', right: '1%',}}> 
   // <img   src={Logout} style={{ width: '25px', height: '25px' }} alt="Logout"  /> 
   // </button>*/}
    <div  style={{top:'5% ' , left:'2px' , position:'absolute'}}><Sidebar/></div>
    
    <div class="col-md mb-4">
     <div   class="form-outline">
     <h1 style={{color : 'white' ,position:'absolute',  top:'3%', left:'15%'}}> Create your own Chatbot now ! </h1>
     <div style={{border: '1px solid white',padding :'10px',top:' 10%', left: '17%' ,position:'absolute'}}>
    
     <input type="text"  class="form-control form-control-lg" value={textInput} onChange={handleTextChange}  placeholder='Chatbot name ' />
     <div style={{marginTop:"3%"}}>
     <label className='text text-light ' style={{ marginRight:"2%"}}> Select your chatbot name color : </label>
     <input type="color"      value={color1} onChange={handleColorChange1}/></div>
     
     <div style={{marginTop:"3%"}}>
     <label className='text text-light ' style={{ marginRight:"2%"}}> Select the sender message color : </label>
     <input type="color"      value={color2} onChange={handleColorChange2}/></div>
     <div style={{marginTop:"3%"}}>
     <label className='text text-light ' style={{ marginRight:"2%"}}> Select the chatbot message color : </label>
     <input type="color"      value={color3} onChange={handleColorChange3}/></div>
     <div style={{marginTop:"3%"}}>
     <label className='text text-light ' style={{ marginRight:"2%"}}> Select the header color : </label>
     <input type="color"      value={color4} onChange={handleColorChange4}/></div>
     <div style={{marginTop:"3%"}}>
     <label className='text text-light ' style={{ marginRight:"2%"}}> Select the body color : </label>
     <input type="color"      value={color5} onChange={handleColorChange5}/></div>
      <button className='btn btn-outline-light'> <input type="file" accept=".png,.jpg,.jpeg" onChange={handleFileChange} /> </button>
      <br></br>
      
      <select class="form-select" aria-label="select the font family" placeholder='select the font family ' style={{ marginTop:"2%"}} value={selectedFont} onChange={ handleFontChange}>
      <option value="1">serif</option>
     <option value="2"> sans-serif</option>
     <option value="3"> system-ui</option>
     <option value="3">ui-serif</option>
     <option value="3">ui-sans-serif</option>
     <option value="3">cursive</option>
     <option value="3">Georgia,  serif</option>
      </select>
      <textarea class="form-control" id="exampleFormControlTextarea1" rows="3" value={textInput2}  style={{ marginTop:"2%"}}  placeholder='Chatbot function expl : I want this chatbot understand in.... ' ></textarea>
     </div>
     <br></br>
     </div>   </div>
     <div   style={{position:'static'  , marginLeft:'50%'}}> 
     
       
     <section style={{backgroundColor : 'transparent' , fontFamily : selectedFont }} >
    <div class="container py-5 " style={{ width: '200%', height:' auto', fontFamily : selectedFont }}>
  
      <div class="row d-flex justify-content-center"  className={isChatOpen ? 'chat-container' : 'chat-container minimized'}>
        <div class="col-md-8 col-lg-6 col-xl-4" >
       
          <div class="card" id="chat1" style={{ borderRadius: '10px'}}>
            <div 
              class="card-header d-flex justify-content-between align-items-center p-3  text-white border-bottom-0"
              style={{ borderTopLeftRadius: '5px' ,  borderTopRightRadius: '0px' , backgroundColor : color4}}>
              <i class="fas fa-angle-left"></i>
              <p class="mb-0 fw-bold" style={{color: color1}}>{textInput}  </p>
              <i class="fas fa-times"></i>
              <button type="button" class="btn-close " aria-label="Close"  onClick={handleToggleChat}></button>
              

            </div>
            <div class="card-body" style={{ backgroundColor : color5}}>
  
              <div class="d-flex flex-row justify-content-start mb-4"  >
              {logoSrc ? 
              <img className="rounded-circle bg-outline-light logo" src={logoSrc} alt="avatar 1" style={{width:' 30px' , height: '100%' }}/> : <p>No logo selected</p>
              }
                <div class="p-3 ms-3" style={{ borderRadius: '15px' , backgroundColor: ' #03224c' , color : 'white'}}>
                <p class="small mb-0">Hello, Please start the changes to create your own chatbot.</p>
                 </div>
              </div>
  
              <div class="d-flex flex-row justify-content-end mb-4">
              <div class="p-3 ms-6" style={{ borderRadius: '15px' , backgroundColor: color2 , fontFamily : selectedFont }}>
                <p class="small mb-0">This is the sender message </p>
                </div>
                
                <img  className="rounded-circle bg-outline-light logo" src={Logo} alt="avatar 1" style={{width: '30px' , height: '100%'}}/>
                  
              </div>
  
              <div class="d-flex flex-row justify-content-start mb-4">
              {logoSrc ? 
              <img className="rounded-circle bg-outline-light logo" src={logoSrc} alt="avatar 1" style={{width:' 30px' , height: '100%' }}/> : <p>No logo selected</p>
              }
               <div class="p-3 ms-6" style={{ borderRadius: '15px' , backgroundColor: color3 , color : 'white'}}>
                
                  <p class="small mb-0">this is your chatbot messsage </p></div>
                <div class="ms-3" style={{borderradius:'15px'}}>
                  <div class="bg-image">
                    <a href="#!">
                      <div class="mask"></div>
                    </a>
                    </div>
                </div>
              </div>
  
              <div class="d-flex flex-row justify-content-start mb-4">
                
              </div>
  
              <div class="form-outline">
                <textarea class="form-control" id="textAreaExample" rows="4" placeholder='Type your message'></textarea>
               
              </div>
  
            </div>
          </div>
  
        </div>
      </div>
      <head>
    <meta charset="utf-8"/>
    <meta http-equiv="x-ua-compatible" content="ie=edge"/>
    <meta name="viewport" content="width=device-width, initial-scale=1"/>
    <meta name="description"/>

    <title>chatbot</title>
    <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0-alpha1/dist/css/bootstrap.min.css" rel="stylesheet" integrity="sha384-GLhlTQ8iRABdZLl6O3oVMWSktQOp6b7In1Zl3/Jr59b6EGGoI1aFkw7cmDA6j6gD" crossorigin="anonymous"></link>
  </head>
  <body>

    <header>
  
  <nav class="navbar navbar-expand-md navbar-dark fixed-top bg-dark">
    <div class="container-fluid">
      <a class="navbar-brand" href="#">chatbot </a>
      <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarCollapse" aria-controls="navbarCollapse" aria-expanded="false" aria-label="Toggle navigation">
        <span class="navbar-toggler-icon"></span>
      </button>
      <div class="collapse navbar-collapse" id="navbarCollapse">
        <ul class="navbar-nav me-auto mb-2 mb-md-0">

        </ul>
      </div>
    </div>
  </nav>
</header>


<main class="flex-shrink-0">
  <div class="container">
    <br></br>
    <br></br>
    <br></br>
    <br></br>
    <form id="chatForm"style={{}}>
    
    <input type="text" id="userPrompt" placeholder= " type your prompt !"/>
    <button type="submit" id="submit-button">Submit</button>
    </form>
    <h1 class="mt-5">Get Started </h1>
    
    <ul>
      <li>OpenAI API Key, which you can get here: <a href="https://openai.com/product#made-for-developers">OpenAI Website</a></li>
    </ul>


    <div id="list-group" class="list-group w-auto">
     
     </div>

     <div class="input-group mb-3">
        <input type="text" class="form-control" id="chat-input"/>
        <div class="input-group-append">
          <button id="gpt-button" class="btn btn-primary">Ask Chatbot</button>
        </div>
      </div>


  </div>
</main>


    <script src="https://code.jquery.com/jquery-3.6.3.min.js" integrity="sha256-pvPw+upLPUjgMXY0G+8O0xUf+/Im1MZjXxxgOcBQBXU=" crossorigin="anonymous"></script>
    <script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0-alpha1/dist/js/bootstrap.bundle.min.js" integrity="sha384-w76AqPfDkMBDXo30jS1Sgez6pr3x5MlQ1ZAGC+nuZB+EYdgRZgiwxhTBTkF7CXvN" crossorigin="anonymous"></script>
</body>
    </div>
  </section>
  
          
 </div>
  
    
     
   
 
    
  </div>


  
  )
}

export default Dashboard
