import React , {useState} from 'react'
import Sidebar from '../components/Sidebar'
import Logout from '../assets/logout.png'

function Dashboard() {
  const [color, setColor] = useState('#fff');
  const handleColorChange = (event) => {
    setColor(event.target.value);
  };
  return (

   <div>
  
    <button className=' btn btn-outline-light' style={{  position: 'fixed',top:' 7%', right: '1%',}}> 
    <img   src={Logout} className='img' style={{ width: '25px', height: '25px' }} alt="Logout"  /> 
    </button>
    <div><Sidebar/></div>
    <div>
     <div className='container' style={{border: '1px solid white',padding :'10px',position: 'fixed',top:' 7%', left: '15%'}}>
    
     <input type="text" class="form-control form-control-lg"  placeholder='Chatbot name '/>
  
     <div style={{marginTop:"3%"}}>
     <label className='text text-light ' style={{ marginRight:"2%"}} >Select your chatbot name  color :  </label>
     <input type="color"      value={color}
        onChange={handleColorChange}/></div>
     </div>
   
    </div>

  
</div>

  
  )
}

export default Dashboard
