import React  , { useState}from 'react'
import Sidebar from '../components/Sidebar'
import photoprofil from '../assets/user.png'
import Chatbots from './chatbots'
function Profil() {

    const [imagePreview, setImagePreview] = useState(photoprofil);
  
    const handleImageChange = (event) => {
      const file = event.target.files[0];
      const reader = new FileReader();
  
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
  
      if (file) {
        reader.readAsDataURL(file);
      }
    };
  
    const handleImageClick = () => {
      const fileInput = document.getElementById('profileImageInput');
      fileInput.click();
    };
      const [Firstname, setName] = useState('John ');
      const [Lastname, setlastName] = useState(' Doe');
      const [email, setEmail] = useState('johndoe@example.com');
      const [showLabels, setShowLabels] = useState(false);
      const [newNameInput, setNewNameInput] = useState('');
      
      const [newLastNameInput, setNewLastNameInput] = useState('');
      const [newEmailInput, setNewEmailInput] = useState('');    
      const handleButtonClick = () => {
        setShowLabels(!showLabels);
      };
      const handleNameInputChange = (event) => {
        setNewNameInput(event.target.value);
      };
      const handleLastNameInputChange = (event) => {
        setNewLastNameInput(event.target.value);
      };
      const handleEmailInputChange = (event) => {
        setNewEmailInput(event.target.value);
      };
    
      const handleSaveChanges = () => {
        if (newNameInput !== '') {
          setName(newNameInput);
        }
        if (newLastNameInput !== '') {
          setlastName(newLastNameInput);
        }
        if (newEmailInput !== '') {
          setEmail(newEmailInput);
        }
        setShowLabels(false);
        setNewNameInput('');
        setNewLastNameInput('');
        setNewEmailInput('');
      };
  return (
   <div style={{display: 'grid' , gridTemplateColumns: '1fr 1fr', justifyContent: 'space-between'}}>
     <div style={{position : 'fixed' , top:'10%' , zIndex:'1'}}><Sidebar/></div>
     <div>
        <div style={{backgroundColor: 'transparent' , top: '5%' , left:'3%' , position:'fixed'}}>

   <div style={{left:'0%'}}>
   <section class="h-100 gradient-custom-2">
  <div class="container py-5 h-100">
    <div class="row d-flex justify-content-center align-items-center h-100">
      <div class="col col-lg-9 col-xl-7">
        <div class="card">
          <div class="rounded-top text-white d-flex flex-row bg-transparent" style={{backgroundColor: 'transparent'}}>
            <div class="ms-4 mt-5 d-flex flex-column" style={{width: '150px'}}>
             
                <div>
                <input type="file" accept="image/*" onChange={handleImageChange}  style={{ display: 'none' , zIndex:'1' }} id="profileImageInput" />
                <label htmlFor="profileImageInput" style={imagePreview ? { display: 'none' } : {}}> Ajouter une image de profil</label>
                {imagePreview ? (
               <img src={imagePreview}  alt="Imagedeprofil" style={{width: '100px', height: '100px' , marginTop: '10px', cursor: 'pointer' }} onClick={handleImageClick} />
                 ) : null}    </div>

              <button type="button" class="btn btn-outline-dark" data-mdb-ripple-color="light" onClick={handleButtonClick}
                style={{zIndex: '1'}}>
                 Edit profile
              </button>
              
            </div>
            
            <div  className="ms-3 text-dark"  >
            <h5 > { Firstname} {Lastname} </h5> 
            <p>Email</p> </div>
          
            {showLabels && (
            <div class="ms-3 text-dark" style={{marginTop: '130px' }}>
              
                    <div class="form-outline">
                      <input type="text" id="form3Example1m" class="form-control form-control-lg" value={newNameInput} onChange={handleNameInputChange}    placeholder='FirstName '/>
                      <input type="text" id="form3Example1m" class="form-control form-control-lg" value={newLastNameInput} onChange={handleLastNameInputChange}    placeholder='LastName '/>
                   
                    </div>
                 
                  <div class="col-md mb-4">
                    <div class="form-outline">
                      <input type="Password" id="form3Example1n" class="form-control form-control-lg" placeholder='Password ' />
                      <button onClick={handleSaveChanges}>Save Changes</button>
                    </div>
                  </div>
            </div>
            )}
          </div>
          <div class="p-4 text-black" style={{backgroundColor: '#f8f9fa'}}>
            <div class="d-flex justify-content-end text-center py-1">
              <div>
                <p class="mb-1 h5">0</p>
                <p class="small text-muted mb-0">chatbots</p>
                </div>
            </div>
          </div>
          <div class="card-body p-4 text-black">
           
            
            <div class="d-flex justify-content-between align-items-center mb-4">
              <p class="lead fw-normal mb-0">Recent Work</p>
              <p class="mb-0"><a href="/Chatbots" class="text-muted">Show all</a></p>
            </div>
            <div class="row g-2">
              <div class="col mb-2">
                <img src="https://mdbcdn.b-cdn.net/img/Photos/Lightbox/Original/img%20(112).webp"
                  alt="image1" class="w-100 rounded-3"/>
              </div>
              <div class="col mb-2">
                <img src="https://mdbcdn.b-cdn.net/img/Photos/Lightbox/Original/img%20(107).webp"
                  alt="image1" class="w-100 rounded-3"/>
              </div>
            </div>
          
          </div>
        </div>
      </div>
    </div>
  </div>
</section>
</div>
        </div></div>
            </div>
        
  
   

    
     
   
    
 
  )
}

export default Profil
