import React ,  { useState, useEffect } from 'react'
import axios from 'axios';
import Sidebar  from '../components/Sidebar'
import '../styles/chatbot.css' ;
import Logout from '../assets/logout.png'
import { Link } from 'react-router-dom';  
function Chatbots() {
  const [chatbots, setChatbots] = useState([])
  const [formData, setFormData] = useState({
    name: '',
    prompt: '',
    senderColor: '',
    chatbotmsgColor: '',
    headerColor: '',
    bodyColor: '',
    photo_data: '',
    nameColor: '',
  });
  useEffect(() => {
    async function fetchChatbots() {
      try {
        const token = localStorage.getItem('token'); 
        const headers = {
          Authorization: `Bearer ${token}`,
        };

        const response = await axios.get('/chatbots', {
          headers: headers,
        });

        setChatbots(response.data);
      } catch (error) {
        console.error('Error fetching chatbots:', error);
      }
    }

    fetchChatbots();
  }, []);
  const handleLogout = async () => {
    try {
          await axios.get('http://127.0.0.1:5000/logout');

       
        window.location.href = '/';  } catch (error) {
        console.error('Logout error:', error);
        
    }
};
const handleUpdateChatbot = async (chatbotId) => {
  try {
    const isAuthenticated = localStorage.getItem('token');
    const headers = {
      Authorization: `Bearer ${isAuthenticated}`,
    };

    const response = await axios.put(`http://127.0.0.1:5000/updatechatbot/${chatbotId}`, formData, {
      headers: headers,
    });

    console.log('Chatbot Updated successfully:', response.data);
  } catch (error) {
    console.error('Error updating chatbot:', error);
  }
};
const [currentPage, setCurrentPage] = useState(1);
const itemsPerPage = 10;
const startIndex = (currentPage - 1) * itemsPerPage;
const endIndex = startIndex + itemsPerPage;
  return (
    <div > 
    <div  style={{top:'11% '  , position:'absolute'}}><Sidebar/></div>
    <button className=' btn btn-outline-light' style={{ position:'absolute',  top:'8%', left: '90%', fontSize: '12px' }} onClick={handleLogout}> 
         <img   src={Logout} style={{ width: '25px', height: '25px' }} alt="Logout"  /> 
       
       </button>
    
    <div style={{ top:'13.5%' , zIndex:'1', display: 'flex',left:'15%',overflowX:'auto',maxWidth:'60%',
      justifyContent: 'center',  position:'absolute' , overflowY:'auto'}}>
      <table  class="table ">
        <thead>
        <tr>
      <th scope="col"></th>
      <th scope="col"></th>
      <th scope="col">name</th>
      <th scope="col"></th>
      <th scope="col"></th>
    </tr>
  </thead>
  
  <tbody >
  {chatbots.slice(startIndex, endIndex).map((chatbot, index) => (
  
              <tr key={chatbot.id}>

                <th scope="row">{index + 1}</th>
                
                <td>  <div className="avatar">
          <img src={chatbot.photo_data} alt="Chatbot Avatar" />
        </div> </td>
          <td> {chatbot.name}</td>
          <td>{console.log(chatbot.id) || chatbot.id}</td>
         
                <td>
                  <Link to={`/Chatpage/${chatbot.id}`} className="btn btn-gradient mr-6"  style={{ marginRight: '5px' }} >Read</Link> 
                  <Link to={`/dashboard/${chatbot.id}/update`} className="btn btn-gradient mr-2">Update</Link>{' '} 
                 <button className="btn btn-gradient">Delete</button>
                </td>
              </tr>
            ))}
    </tbody>
  
<tfoot>
    <div style={{position:'relative', textAlign: 'center' }}>
  <button
    onClick={() => setCurrentPage(currentPage - 1)}
    disabled={currentPage === 1}
  >
    Previous
  </button>
  <span style={{ margin: '0 10px' }}>{currentPage}</span>
  <button
    onClick={() => setCurrentPage(currentPage + 1)}
    disabled={endIndex >= chatbots.length}
  >
    Next
  </button>
</div></tfoot>
        </table>
      
    </div>
   

    </div>
  )
}

export default Chatbots
