import React ,  { useState, useEffect } from 'react'
import axios from 'axios';
import Sidebar  from '../components/Sidebar'
import '../styles/chatbot.css' ;
function Chatbots() {
  const [chatbots, setChatbots] = useState([])
  useEffect(() => {
    async function fetchChatbots() {
      try {
        const token = localStorage.getItem('token'); // Assurez-vous que le token est stocké dans le local storage
        const headers = {
          Authorization: `Bearer ${token}`,
        };

        const response = await axios.get('/chatbots', {
          headers: headers,
        });

        setChatbots(response.data);
      } catch (error) {
        console.error('Error fetching chatbots:', error);
      }
    }

    fetchChatbots();
  }, []);
  return (
    <div > 
    <div  style={{top:'8% '  , position:'absolute'}}><Sidebar/></div>
    
     <h2 style={{ color: 'white' , marginTop:'10%' , textAlign: 'center' ,marginLeft:'17%' , position: 'absolute'}}> Your chatbots :</h2>
    <br></br>
    <div style={{backgroundColor : 'transparent' ,top:'20%' , zIndex:'1', display: 'flex',left:'10%', maxWidth:'90%',
      justifyContent: 'center', alignItems:'center' , padding: '50px',  paddingTop: '40px' , position:'relative' , overflow:'auto' }}>
    
        <table  class="table bg-transparent">
        <thead>
    <tr >
      <th scope="col"></th>
      <th scope="col">Name</th>
      <th scope="col">URL</th>
      <th scope="col"></th>
    </tr>
  </thead>
  <tbody>
  {chatbots.map((chatbot, index) => (
              <tr key={chatbot.id}>
                <th scope="row">{index + 1}</th>
                <td>  <div className="avatar">
          <img src={chatbot.photo_data} alt="Chatbot Avatar" />
        </div>{chatbot.name}</td>
                <td>{chatbot.url}</td>
                <td>
                  <button className="btn btn-gradient">Update</button>{' '}
                  <button className="btn btn-gradient">Delete</button>
                </td>
              </tr>
            ))}
    </tbody>


        </table>

    </div>
    </div>
  )
}

export default Chatbots
