import React ,  { useState, useEffect } from 'react'
import axios from 'axios';
import Sidebar  from '../components/Sidebar'
import '../styles/chatbot.css' ;
import Logout from '../assets/logout.png'
function Chatbots() {
  const [chatbots, setChatbots] = useState([])
  useEffect(() => {
    async function fetchChatbots() {
      try {
        const token = localStorage.getItem('token'); // Assurez-vous que le token est stocké dans le local storage
        const headers = {
          Authorization: `Bearer ${token}`,
        };

        const response = await axios.get('/chatbots', {
          headers: headers,
        });

        setChatbots(response.data);
      } catch (error) {
        console.error('Error fetching chatbots:', error);
      }
    }

    fetchChatbots();
  }, []);
  const handleLogout = async () => {
    try {
        // Make a GET request to the logout endpoint
        await axios.get('http://127.0.0.1:5000/logout');

        // Perform any additional cleanup or redirect if needed
        // For example:
        window.location.href = '/'; // Redirect to the login page after logout
    } catch (error) {
        console.error('Logout error:', error);
        // Handle the error here
    }
};
  return (
    <div > 
    <div  style={{top:'8% '  , position:'absolute'}}><Sidebar/></div>
    <button className=' btn btn-outline-light' style={{ position:'absolute',  top:'8%', left: '90%', fontSize: '12px' }} onClick={handleLogout}> 
         <img   src={Logout} style={{ width: '25px', height: '25px' }} alt="Logout"  /> 
       
       </button>
     <h2 style={{ color: 'white' , top:'10%' , textAlign: 'center' ,left:'13%' , position: 'absolute'}}> Your chatbots :</h2>
    <br></br>
    <div style={{ top:'20%' , zIndex:'1', display: 'flex',left:'10%', maxWidth:'90%',
      justifyContent: 'center', alignItems:'center' , padding: '50px',  paddingTop: '40px' , position:'relative' , overflowY:'auto' }}>
    
        <table  class="table ">
        <thead>
    <tr >
      <th scope="col"></th>
      <th scope="col"></th>
      <th scope="col">Name</th>
      <th scope="col">URL</th>
      <th scope="col"></th>
    </tr>
  </thead>
  <tbody>
  {chatbots.map((chatbot, index) => (
              <tr key={chatbot.id}>
                <th scope="row">{index + 1}</th>
                <td>  <div className="avatar">
          <img src={chatbot.photo_data} alt="Chatbot Avatar" />
        </div> </td>
          <td> {chatbot.name}</td>
                <td>{chatbot.url}</td>
                <td>
                  <button className="btn btn-gradient">Read</button>{' '}
                  <button className="btn btn-gradient">Update</button>{' '}
                  <button className="btn btn-gradient">Delete</button>
                </td>
              </tr>
            ))}
    </tbody>


        </table>

    </div>
    </div>
  )
}

export default Chatbots
