import React , {useState} from 'react'
import {BrowserRouter as Router ,Route , Routes ,Link , useNavigate } from 'react-router-dom'; 
import Logo from '../assets/logo.png'
import Signin from './signin';
import '../styles/signup.css'; 
import axios from 'axios';

function Signup() {
 
  const [formData, setFormData] = useState({
    Firstname: '',
    Lastname:'',
    email: '', 
    password: ''
  });
  const [message, setMessage] = useState('');
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };
  const navigate = useNavigate();
  const handleSubmit = async () => {
    try {
        const response = await axios.post('/register', formData); // Send POST request to Flask API endpoint
        if (response.status === 200) {
          console.log('User registered successfully:', response.data);
          navigate.push('/dashboard'); // Redirect to dashboard on successful signup
      } else {
          console.log('Signup failed:', response.data);
      }
  } catch (error) {
      console.error('Error during signup:', error);
  }
};
  return (
    
    <form className="signup-form">
    <div className="image-container">
      <img src={Logo} className="logo" alt="Logo Chatbotyy" />
    </div>
    <div className="form-container">
      <div className="form-outline form-inline">
        <input type="text" id="firstName" className="form-control form-control-lg" placeholder="First Name" />
     
        <input type="text" id="lastName" className="form-control form-control-lg" placeholder="Last Name" />
      </div>
      <div className="form-outline">
        <input type="text" id="email" className="form-control form-control-lg" placeholder="Email Address" />
      </div>
      <div className="form-outline">
        <input type="password" id="password" className="form-control form-control-lg" placeholder="Password" />
      </div>
    </div>
    <div className="button-container">
      <button type="button" className="btn btn-outline-light btn-block btn-lg mb-6" onClick={handleSubmit}>
        Sign up
      </button>
    </div>
    <br></br>
    <div className="text-center text-white">
      <p>
        Already a member? <Link to="/Signin" className="text-light">Sign In Now</Link>
      </p>
      <Routes>
        <Route path="/Signin" element={<Signin />} />
      </Routes>
    </div>
  </form>
 
  )
}

export default Signup
